mapper.select:
select

  distinct


from t_agentpay_passage




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}


  		select (select count(1) from t_mch_agentpay_passage mp
  			    where mp.AgentpayPassageId = #{agentPayPassageId} and mp.MchFeeEvery < #{setFee}
  			    ) as mchCount,
			   (select count(1) from t_agent_agentpay_passage ap
			    where ap.AgentpayPassageId = #{agentPayPassageId} and ap.FeeEvery < #{setFee}
			    )  as agentCount
	    from dual
