mapper.select:
select

  distinct


from t_mch_trade_order




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select

  distinct


,(select ChannelOrderNo from t_pay_order p where p.PayOrderId = m.PayOrderId limit 1) ChannelOrderNo
from t_mch_trade_order m




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select
,
from t_mch_trade_order where TradeOrderId = #{tradeOrderId,jdbcType=VARCHAR}

SELECT
ifnull(SUM(Amount), 0)  AS totalAmount,
ifnull(SUM(MchIncome), 0)  AS totalMchIncome,
ifnull(SUM(DiscountAmount), 0)  AS totalDiscountAmount,
ifnull(SUM(RefundTotalAmount), 0)  AS totalRefundAmount,
COUNT(1) AS totalCount
FROM t_mch_trade_order
WHERE 1 = 1
 AND MchId = #{mchId} 
 AND StoreId = #{storeId} 
 AND TradeOrderId = #{tradeOrderId} 
 AND PayOrderId = #{payOrderId} 
 AND TradeType = #{tradeType} 
 AND Status = #{status} 
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} ;

select count(1) from t_mch_trade_order
where MemberId != ''

 and CreateTime >= #{beginTime}    

 and CreateTime <= #{endTime}    

 and MchId = #{mchId}

( select count(1) as totalCount from t_mch_trade_order where Status in (2, 4, 5) and MemberId is not null and TradeType = 1
) union all ( select count(1) as totalCount from t_mch_trade_order where Status in (2, 4, 5) and MemberId is null and TradeType = 1
)

select IsvDeviceNo as isvDeviceNo, count(1) as orderCount, ifnull(sum(Amount - RefundTotalAmount), 0) as orderTotalAmount from t_mch_trade_order where Status in (2, 4, 5) and IsvId = #{isvId} and IsvDeviceNo in
#{isvDeviceNo}
GROUP BY IsvDeviceNo
