mapper.select:
select

  distinct


from t_sett_record




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

SELECT SUM(SettAmount) AS totalSettAmount,
SUM(SettFee) AS totalSettFee,
COUNT(1) AS totalCount
FROM t_sett_record
WHERE SettStatus IN (5)
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} 
;

SELECT
SUM(SettAmount) AS totalAmount,
SUM(SettFee) AS totalFee,
COUNT(1) AS totalCount
FROM t_sett_record
WHERE 1 = 1
 AND InfoId = #{infoId} 
 AND SettOrderId = #{settOrderId} 
 AND AccountName like #{accountName} 
 AND SettStatus = #{settStatus} 
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} ;

SELECT tsr.infoId AS mchId,
tmi.Name AS mchName,
SUM(tsr.SettAmount) AS totalAmount,
SUM(tsr.RemitAmount) AS totalRemitAmount,
COUNT(1) AS totalCount
FROM `t_sett_record` tsr
INNER JOIN t_mch_info tmi ON tsr.infoId = tmi.MchId
WHERE tsr.SettStatus = 5
AND tsr.InfoType = 2
 AND tsr.infoId = #{mchId} 
 AND tsr.CreateTime >= #{createTimeStart} 
 AND tsr.CreateTime <= #{createTimeEnd} 
GROUP BY tsr.infoId ORDER BY totalAmount DESC


    limit ${offset}, ${limit}


    limit ${limit}

;

SELECT
SUM(SettAmount) AS totalAmount,
SUM(RemitAmount) AS totalRemitAmount,
COUNT(1) AS totalCount,
COUNT(distinct infoId) AS totalMchCount
FROM `t_sett_record`
WHERE `SettStatus` = 5
AND InfoType = 2
 AND infoId = #{mchId} 
 AND CreateTime >= #{createTimeStart} 
 AND CreateTime <= #{createTimeEnd} 
ORDER BY totalAmount DESC;
